            if (typeof w2ui[this.name] == 'undefined') return false;
            // clean up
            if ($(this.box).find('#layout_' + this.name + '_panel_main').length > 0) {
                $(this.box)
                    .removeAttr('name')
                    .removeClass('w2ui-layout')
                    .html('');
            }
            delete w2ui[this.name];
            $(this.box).remove();
            // event after
            this.trigger($.extend(eventData, {
                phase: 'after'
            }));
