                (this.header != '' ? '<div class="w2ui-form-header">' + this.header + '</div>' : '') +
                '    <div id="form_' + this.name + '_toolbar" class="w2ui-form-toolbar"></div>' +
                '    <div id="form_' + this.name + '_tabs" class="w2ui-form-tabs"></div>' +
                this.formHTML +
                '</div>';
